<div class="nk-sign-form" style="padding-top: 147px; display: none; opacity: 0; transform: translate3d(0px, 0px, 0px);">
    <div class="nk-gap-5"></div>
    <div class="container">
        <div class="row">
            <div class="col-lg-4 offset-lg-4 col-md-6 offset-md-3">
                <div class="nk-sign-form-container" style="opacity: 1; transform: translate3d(0px, 0px, 0px);">
                    <div class="nk-sign-form-toggle h3">
                        <a href="#" class="nk-sign-form-login-toggle active">Log In</a>
                        <a href="#" class="nk-sign-form-register-toggle">Register</a>
                    </div>
                    <div class="nk-gap-2"></div>
                    <!-- START: Login Form -->
                    <form class="nk-sign-form-login active" novalidate="novalidate">
                        <input class="form-control login-username" type="text" placeholder="Username">
                        <div class="nk-gap-2"></div>
                        <input class="form-control login-password" type="password" placeholder="Password">
                        <div class="nk-gap-2"></div>
                        <div class="form-check pull-left">
                            <label class="form-check-label">
                                <input type="checkbox" class="form-check-input login-remember" checked=""> Remember Me
                            </label>
                        </div>
                        <div class="clearfix login-submit-container"><button class="nk-btn nk-btn-color-white link-effect-4 pull-right login-submit ready"><span class="link-effect-inner"><span class="link-effect-l"><span>Log In</span></span><span class="link-effect-r"><span>Log In</span></span><span class="link-effect-shade"><span>Log In</span></span></span></button></div>
                        <div class="error-login-submit-container nk-error error-ignore"></div>
                        <div class="nk-gap-1"></div>
                        <a class="nk-sign-form-lost-toggle pull-right" href="#">Lost Password ?</a>
                    </form>
                    <!-- END: Login Form -->
                    <!-- START: Lost Password Form -->
                    <form class="nk-sign-form-lost" novalidate="novalidate">
                        <input class="form-control password-reset-usernameoremail" type="text" placeholder="Username or Email">
                        <div class="nk-gap-2"></div>
                        <button class="nk-btn nk-btn-color-white link-effect-4 pull-right ready"><span class="link-effect-inner"><span class="link-effect-l"><span>Reset Password</span></span><span class="link-effect-r"><span>Reset Password</span></span><span class="link-effect-shade"><span>Reset Password</span></span></span></button>
                    </form>
                    <!-- END: Lost Password Form -->
                    <!-- START: Register Form -->
                    <form class="nk-sign-form-register" novalidate="novalidate">
                        <input class="form-control register-username" type="text" placeholder="Username" title="" data-toggle="tooltip" data-animation="false" data-original-title="Username - the name you will use for authentication. Will not be publicly displayed.">
                        <div class="nk-gap-2"></div>
                        <input class="form-control register-display-name" type="text" placeholder="Display name" title="" data-toggle="tooltip" data-animation="false" data-original-title="Display name - your public name">
                        <div class="nk-gap-2"></div>
                        <input class="form-control register-email" type="email" placeholder="Email address" title="" data-toggle="tooltip" data-animation="false" data-original-title="Email address - used for confirming your account. Will not be publicly displayed.">
                        <div class="nk-gap-2"></div>
                        <input class="form-control register-password" type="password" placeholder="Password" title="" data-toggle="tooltip" data-animation="false" data-original-title="Password">
                        <div class="nk-gap-2"></div>
                        <input class="form-control register-repeat-password" type="password" placeholder="Repeat password" title="" data-toggle="tooltip" data-animation="false" data-original-title="Repeat password">
                        <div class="nk-gap-2"></div>
                        <input class="form-control register-referrer" type="text" placeholder="Referrer" title="" data-toggle="tooltip" data-animation="false" data-original-title="Referrer - the display name of the player who referred you to Shaiya Diverge. Leave blank if none.">
                        <div class="nk-gap-2"></div>
                        <div class="clearfix register-submit-container"><button class="nk-btn nk-btn-color-white link-effect-4 pull-right register-submit ready"><span class="link-effect-inner"><span class="link-effect-l"><span>Register</span></span><span class="link-effect-r"><span>Register</span></span><span class="link-effect-shade"><span>Register</span></span></span></button></div>
                        <div class="error-register-submit-container nk-error error-ignore"></div>
                    </form>
                    <!-- END: Register Form -->
                </div>
            </div>
        </div>
    </div>
    <div class="nk-gap-5"></div>
</div>